CREATE TABLE blog_comments (id BIGINT AUTO_INCREMENT, commentary TEXT NOT NULL, photo TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE blog_posts (id BIGINT AUTO_INCREMENT, post TEXT NOT NULL, photo TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE blog_users (id BIGINT AUTO_INCREMENT, user_id BIGINT, name VARCHAR(255) NOT NULL, pass VARCHAR(255) NOT NULL, messages BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
